import { Component, Input } from '@angular/core';
import { ApiServiceService } from 'src/app/service/api-service.service';

@Component({
  selector: 'app-controle',
  templateUrl: './controle.component.html',
  styleUrls: ['./controle.component.css']
})
export class ControleComponent {

  _apiOnline!: boolean;

  constructor(private apiService: ApiServiceService) { }

  @Input() set apiOnline(value: boolean) {
    
    this._apiOnline = value; 
 }

  public restartApi() {
    this.apiService.restart().subscribe();
  } 
}
<body>
    <div [ngClass]="{'hide':!apiOnline, 'show':apiOnline}" class="form">
        <form>
            <div class="ip">
                <input type="text" placeholder="IP do arduino" id="ip" name="ip">
            </div>
            <br>
            <div class="token">
                <input type="text" placeholder="Token do Bot" id="token" name="token">
            </div>
            <br>
            <div class="botao">
                <button type="reset" class="redefinir">Redefinir</button>
                <button (click)="restartApi()" type="reset" class="resetar">Resetar API</button>
            </div>
        </form>
    </div>
</body>
